class:
Ext.define('${appName}.store.${shortPackage}.${modelName}Store', {
    extend: 'Ext.data.Store',
    autoLoad: true,
    model: '${appName}.model.${shortPackage}.${modelName}',
    remoteSort: true,
    pageSize: 20,
    proxy: {
        type: 'ajax',
        api: {
            read: 'rest/${shortPackage}/${modelName}/listBy.json' //读取
        },
        actionMethods: {
            // by default GET
            read   : 'POST'
        },
        reader: {
            type: 'json',
            root: 'data',
            totalProperty: 'totalCount'
        },
        limitParam: 'pageSize'
    }
});
